import styled from 'styled-components';

export const SignInContainer = styled.div`
.sign-in-container {
    display: flex;
    flex-direction: column;
    width: 380px;

    h2 {
        margin-top: 10px 0px;
    }

    .buttons-container {
        display: flex;
        justify-content: space-between;
    }
}`;
