import { useState, useContext } from "react";
import Button from "../button/button.component";
import FormInput  from "../form-input/form-input.component";
import './sign-up-form.styles.scss';


import { createAuthUserWithEmailAndPassword, createUserDocumentFromAuth} from "../../utils/firebase/firebase.utils";

const defaultFormFields = {
    displayName: '',
    email: '',
    password: '',
    confirmPassword: '',
}

const SignUpForm = () => {

    const [formFields, setFormFields] = useState(defaultFormFields);
    const { displayName, email, password, confirmPassword } = formFields;


    const resetFormFields = () => {
        setFormFields(defaultFormFields);
    }

    const handleSubmit = async (event) => {
        // Prevent some kind of action or behavior in the browser, such as navigating to a new page or submitting a form and refreshing the page.
        event.preventDefault();

        if (password !== confirmPassword) {
            alert('Passwords do not match');
            return
        }

        try {
            const {user} = await createAuthUserWithEmailAndPassword(email, password);
            await createUserDocumentFromAuth(user, { displayName });
            setCurrentUser(user);
            resetFormFields()

        } catch(error) {
            if(error.code === 'auth/email-already-in-use'){
                alert('Cannot create user, email already in use')
            } else {
                console.log('user creation encountered an error', error);
            }
        }


    }

    const handleChange = (event) => {
        const {name, value} = event.target;
        // [name] here is a computed property, otherwise they key would be set to 'name' specifically.
        setFormFields({...formFields, [name]:value});
    };

    return (
        <div className="sign-up-container">
            <h2>Don't have an account?</h2>
            <span>Sign Up With Your Email and Password</span>
            <form onSubmit={handleSubmit}>
                <FormInput
                    label='Display Name'
                     type='text' 
                     onChange={handleChange} 
                     name='displayName' 
                     value={displayName} 
                     required />

                <FormInput
                    label='Email'
                     type='email' 
                     onChange={handleChange} 
                     name='email' 
                     value={email} 
                     required />

                <FormInput
                    label='Password'
                     type='password' 
                     onChange={handleChange} 
                     name='password' 
                     value={password} 
                     required />

                <FormInput
                    label='Confirm Password'
                     type='password' 
                     onChange={handleChange} 
                     name='confirmPassword' 
                     value={confirmPassword} 
                     required />

                <Button type='submit'>Sign Up</Button>
            </form>
        </div>
    )
}

export default SignUpForm;
