import { useState, useContext } from "react";
import Button from "../button/button.component";
import FormInput  from "../form-input/form-input.component";
import './sign-in-form.styles.scss';

import { UserContext } from "../../contexts/user.context";

import { 
    signInWithGooglePopup, 
    createUserDocumentFromAuth,
    signInAuthUserWithEmailAndPassword,
} from "../../utils/firebase/firebase.utils";

const defaultFormFields = {
    email: '',
    password: '',
}

const SignInForm = () => {

    const [formFields, setFormFields] = useState(defaultFormFields);
    const {email, password} = formFields;

    const { setCurrentUser } = useContext(UserContext);

    const resetFormFields = () => {
        setFormFields(defaultFormFields);
    }

    const signInWithGoogle = async() => {
        const {user} = await signInWithGooglePopup();
        await createUserDocumentFromAuth(user);
    }

    const handleSubmit = async (event) => {
        // Prevent some kind of action or behavior in the browser, such as navigating to a new page or submitting a form and refreshing the page.
        event.preventDefault();

        try {
            const {user} = await signInAuthUserWithEmailAndPassword(
                email, 
                password
            );

            setCurrentUser(user);

            resetFormFields()
        } catch(error) {
            switch(error.code) {
                case 'auth/user-not-found':
                    alert('No user associated with this email!');
                    break;
                case 'auth/wrong-password':
                    alert('Incorrect password for email!');
                    break;
                default:
                    console.log(error);
            }
        }


    }

    const handleChange = (event) => {
        const {name, value} = event.target;
        // [name] here is a computed property, otherwise they key would be set to 'name' specifically.
        setFormFields({...formFields, [name]:value});
    };

    return (
        <div className="sign-in-container">
            <h2>Already have an account?</h2>
            <span>Sign In With Your Email and Password</span>
            <form onSubmit={handleSubmit}>
                <FormInput
                    label='Email'
                     type='email' 
                     onChange={handleChange} 
                     name='email' 
                     value={email} 
                     required />

                <FormInput
                    label='Password'
                     type='password' 
                     onChange={handleChange} 
                     name='password' 
                     value={password} 
                     required />

                <div className="buttons-container">
                    <Button type='submit'>Sign In</Button>
                    <Button type='button' onClick={signInWithGoogle} buttonType='google'>Google Sign In</Button>
                </div>
            </form>
        </div>
    )
}

export default SignInForm;
